def create_annotations(text, train, type):
    # Split the text into words
    words = text.split()

    # Initialize annotations list
    annotations = []

    # Iterate over the words and manually create annotations
    current_index = 0
    for word in words:
        start = current_index
        end = current_index + len(word)
        label = type  # "O" means no label, you can replace it with your desired label
        if len(word) > 3 and word.upper() in train:
            annotations.append({'start': start, 'end': end, 'tag_name': label})
        current_index = end + 1  # Increment current index by word length + 1 for space

    return annotations

